mapper.select:
select
from (
select * from tb_certify_resource p start with p.id = 0 connect by prior p.id = p.parent_id
select * from tb_certify_resource p where p.id in ( select distinct t.id from tb_certify_resource t connect by prior t.parent_id = t.id start with t.id in ( select distinct b.id from tb_certify_reso_acco a, tb_certify_resource b where a.account_id = #{accountId,jdbcType=INTEGER} and a.resource_id = b.id))
) q connect by prior q.id = q.parent_id start with q.parent_id = 0 order siblings by q.sort asc
