mapper.select:
select b.id as ID,a.id as GRID_INFO_ID, c.alias_name as alias_name, a.describe GRID_DESC, b.v_code as ITEM_VCODE, b.name as ITEM_NAME, b.d_code as ITEM_DCODE, b.DATATYPE as DATATYPE from tb_conf_grid_info a, tb_conf_grid_items b left join TB_CONF_GRID_ITEM_ALIAS c on b.id = c.item_id and c.company_id = #{companyId} where a.id = b.grid_id and a.id = #{gridInfoId}
and not exists (select 1 from tb_conf_grid_item_company c where c.company_id = #{companyId} and c.item_id = b.id )
order by b.sort

mapper.update:
 	update tb_conf_cost
  set cost_flags = #{flags},
      update_time = sysdate,
      update_id = #{userId},
      update_name = #{userName}
where company_id = #{companyId}

mapper.insert:

      INSERT INTO tb_conf_grid_item_company(ID , COMPANY_ID,ITEM_ID ,SORT) SELECT SEQ_TB_CONF_GRID_ITEM_COMPANY.NEXTVAL ,#{companyId}, A.* FROM (  
        
          SELECT #{item.id} , #{item.sort} FROM DUAL  
        
      ) A
